var math = require('./index');
var assert = require('assert');

describe('Range', function() {
    it('T1', function() {
	assert(math.range(10).length === 10);
    });
    it('T2', function() {
	assert(math.range(10)[9] === 10);
    });
});

describe('Sum', function() {
    it('T1', function()  {
	assert(math.sum(5, 6, 5) === 16);
    });
});

describe('Product', function() {
    it('T1', function() {
	assert(math.product(5, 5, 2) === 50);
    });
});

describe('Exponentials', function() {
    it('T1', function() {
	assert(math.square(5) === 25);
    });
});

describe('Binary', function() {
    it('T1', function() {
	assert(math.bin('11111111') === 255);
    });
    it('T2', function() {
	assert(!math.isBin('23523'));
    });
    it('T3', function() {
	assert(math.isBin('10101'));
    });
});

describe('Sort', function() {
    it('T1', function() {
	assert(JSON.stringify(math.sort(3,2,1)) === JSON.stringify([1, 2, 3]));
    });
});

describe('Median', function() {
    it('T1', function() {
	assert(math.median(1, 2, 3) === 2);
    });
    it('T2', function() {
	assert(math.median(1, 2, 3, 4) === 2.5);
    });
});
